CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;
CREATE TABLE "University" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "ShortName" text NOT NULL,
    "Introduction" text NOT NULL,
    "OfficialWebsite" text NOT NULL,
    "AdmissionWebsite" text NOT NULL,
    "Ranking" integer,
    "RankingCriteria" text NOT NULL,
    "Locations" text,
    CONSTRAINT "PK_University" PRIMARY KEY ("Id")
);

CREATE TABLE "AdmissionMethod" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "Description" text NOT NULL,
    "Criteria" text NOT NULL,
    "Year" integer,
    "UniversityId" integer NOT NULL,
    CONSTRAINT "PK_AdmissionMethod" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_AdmissionMethod_University_UniversityId" FOREIGN KEY ("UniversityId") REFERENCES "University" ("Id") ON DELETE CASCADE
);

CREATE TABLE "AdmissionNew" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Title" text NOT NULL,
    "Content" text NOT NULL,
    "PublishDate" timestamp with time zone NOT NULL,
    "Year" integer,
    "UniversityId" integer NOT NULL,
    CONSTRAINT "PK_AdmissionNew" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_AdmissionNew_University_UniversityId" FOREIGN KEY ("UniversityId") REFERENCES "University" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Major" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "Code" text NOT NULL,
    "Description" text NOT NULL,
    "AdmissionScore" numeric(18,2),
    "Year" integer,
    "UniversityId" integer NOT NULL,
    CONSTRAINT "PK_Major" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Major_University_UniversityId" FOREIGN KEY ("UniversityId") REFERENCES "University" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Program" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "Description" text NOT NULL,
    "Tuition" numeric(18,2),
    "TuitionUnit" text NOT NULL,
    "Year" integer,
    "UniversityId" integer NOT NULL,
    CONSTRAINT "PK_Program" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Program_University_UniversityId" FOREIGN KEY ("UniversityId") REFERENCES "University" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Scholarship" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "Description" text NOT NULL,
    "Value" numeric(18,2),
    "ValueType" text NOT NULL,
    "Criteria" text NOT NULL,
    "Year" integer,
    "UniversityId" integer NOT NULL,
    CONSTRAINT "PK_Scholarship" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Scholarship_University_UniversityId" FOREIGN KEY ("UniversityId") REFERENCES "University" ("Id") ON DELETE CASCADE
);

CREATE TABLE "AdmissionCriteria" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "Description" text NOT NULL,
    "MinimumScore" numeric(18,2),
    "AdmissionMethodId" integer NOT NULL,
    CONSTRAINT "PK_AdmissionCriteria" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_AdmissionCriteria_AdmissionMethod_AdmissionMethodId" FOREIGN KEY ("AdmissionMethodId") REFERENCES "AdmissionMethod" ("Id") ON DELETE CASCADE
);

CREATE INDEX "IX_AdmissionCriteria_AdmissionMethodId" ON "AdmissionCriteria" ("AdmissionMethodId");

CREATE INDEX "IX_AdmissionMethod_UniversityId" ON "AdmissionMethod" ("UniversityId");

CREATE INDEX "IX_AdmissionNew_UniversityId" ON "AdmissionNew" ("UniversityId");

CREATE INDEX "IX_Major_UniversityId" ON "Major" ("UniversityId");

CREATE INDEX "IX_Program_UniversityId" ON "Program" ("UniversityId");

CREATE INDEX "IX_Scholarship_UniversityId" ON "Scholarship" ("UniversityId");

