    CONSTRAINT "PK_ChatMessage" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_ChatMessage_ChatSession_ChatSessionId" FOREIGN KEY ("ChatSessionId") REFERENCES "ChatSession" ("Id") ON DELETE CASCADE
);

